document.addEventListener("DOMContentLoaded",e=>{const n=document.querySelectorAll(".post__content div.highlight"),t="Copy",s="Copied";n.forEach(e=>{const n=document.createElement("button");n.innerHTML=t,n.classList.add("copyCodeButton"),e.appendChild(n);const o=e.querySelector("code[data-lang]");if(!o)return;n.addEventListener("click",()=>{navigator.clipboard.writeText(o.textContent).then(()=>{n.textContent=s,setTimeout(()=>{n.textContent=t},1e3)}).catch(e=>{console.log("Something went wrong",e)})})})})