document.addEventListener("DOMContentLoaded",function(){const s=document.getElementById("toc-button"),t=document.getElementById("toc-overlay"),e=document.getElementById("toc-window"),n=document.querySelector(".post__content");if(!s)return;s.addEventListener("click",function(){e.style.display==="none"||e.style.display===""?(e.style.display="block",t.style.display="block",n.classList.add("blur")):(e.style.display="none",t.style.display="none",n.classList.remove("blur"))}),t.addEventListener("click",function(){e.style.display="none",t.style.display="none",n.classList.remove("blur")}),e.addEventListener("click",function(e){e.stopPropagation()});const o=e.querySelectorAll("a");o.forEach(s=>{s.addEventListener("click",function(){e.style.display="none",t.style.display="none",n.classList.remove("blur")})})})